mapper.select:
SELECT hallid, fcltynm, userid, halladdress, img , hallphone
	FROM hall
	LIMIT #{start}, #{perPage}

		SELECT chgimg, img
        FROM hall
        WHERE hallid = #{hallid}

mapper.update:
UPDATE hall
SET enabled = #{enabled}
WHERE hallid = #{hallid};

UPDATE hall
SET allseatcnt = #{allSeatCnt}
WHERE hallid = #{hallid};
